Inputs.table(newData.map(d =&gt; ({Name: [[d.link,d.name]], ...d})), {
  sort: "start",
  reverse: true,
  columns:[
    "venue_name",
    "location",
    "start_date",
    "end_date"
  ],  
  format: {
    Name: links =&gt; htl.html`${links.map((link, i) =&gt; htl.html`&lt;a href=${link[0]} target=_blank&gt;${link[1]}&lt;/a&gt; `)}`
  },
  layout: "auto",
  header: {
    venue_name: "Conference Name",
    location: "Location",
    start_date: "Start Date",
    end_date: "End Date"
  },
  width: {
    Name: 300,
    location: 300,
    start_date: 300,
    end_date: 300
  },
  align: {
    Name: "center",
    location: "center",
    start_date: "center",
    end_date: "center"
  }
})
